<script>
export default {
    data: () => {
        return {
            imgUrl: require('@/assets/images/xiaoY.png'),
            today: "",
            msg:"小可爱！<br>今天更爱你了一点",
            ip: "",
            popupShow: false,
            kuaidi: "",
            kuaidiID:"",
            emailShow: false,
            to: "",
            mailMsg: "",
            loading: false,
            IDshow: false,
            userID: ""
        };
    },
    created() {
        var self = this;
        var day = new Date();

        this.today = day.getMonth() + "月" + day.getDate() + "日" + "星期" + "日一二三四五六".charAt(new Date().getDay());

        // 获取ip
        this.$http.post('/api').then((result) => {
                self.ip = JSON.parse(result.data.split("= ")[1].split(";")[0]).cip;
        })
    },
    methods: {
        getWeather() {
             this.$router.push('/weather');
        },
        ychat() {
            this.$router.push('/chat');
        },
        deliverGo() {
            this.popupShow = true;
        },
        searchDeliver() {
            
            this.$router.push('/kuaidi/' + this.kuaidiID + '/' + this.kuaidi);
            
        },
        emailShowClick() {
            this.emailShow = true;
        },
        sendEmail() {
            this.loading = true;
            this.$http.get('/loc/user/email?to=' + this.to + '&msg=' + this.mailMsg).then(res => {
                console.log(res);
                
                this.loading = false;
                this.$notify({ type: 'success', message: '发送成功' });
                
            })
        },
        OCRgo() {
            this.$router.push('/ocr');
        },
        chatIDShow() {
            this.IDshow = true;
        },
        goChatRoom() {
            this.$router.push('/room/' + this.userID)
        }
    },
    computed: {

    }
};
</script>


